import React from 'react';
import { useNavigate } from 'react-router-dom';
import ApiService from '../../service/ApiService';

const RoomResult = ({ roomSearchResults }) => {
    const navigate = useNavigate();
    const isAdmin = ApiService.isAdmin();

    return (
        <section className="room-results">
            {roomSearchResults && roomSearchResults.length > 0 && (
                <div className="room-list">
                    {roomSearchResults.map(room => (
                        <div key={room.id} className="room-list-item">
                           <img
    className="room-list-item-image"
    src={room.imagePath}  // <-- must match demoRooms field
    alt={`${room.hotelName} - ${room.roomType}`}
/>

                            <div className="room-details">
                                <h3>{room.roomType}</h3>
                                <p><b>Hotel:</b> {room.hotelName}</p>
                                <p><b>Rating:</b> ‚≠ê {room.rating}</p>
                                <p><b>Price:</b> ${room.roomPrice} / night</p>
                                <p>{room.roomDescription}</p>
                            </div>

                            <div className="book-now-div">
                                {isAdmin ? (
                                    <button
                                        className="edit-room-button"
                                        onClick={() => navigate(`/admin/edit-room/${room.id}`)}
                                    >
                                        Edit Room
                                    </button>
                                ) : (
                                    <button
                                        className="book-now-button"
                                        onClick={() => navigate(`/room-details-book/${room.id}`)}
                                    >
                                        View / Book Now
                                    </button>
                                )}
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </section>
    );
}

export default RoomResult;
