import React, { useState, useEffect } from 'react';
import ApiService from '../../service/ApiService';
import RoomResult from '../common/RoomResult';

const AllRoomsPage = () => {
  const [rooms, setRooms] = useState([]);
  const [filteredRooms, setFilteredRooms] = useState([]);
  const [roomTypes, setRoomTypes] = useState([]);
  const [selectedRoomType, setSelectedRoomType] = useState('');
  const [locations, setLocations] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState('');
  const [showRooms, setShowRooms] = useState(false);

  // Predefined demo rooms with images
  const demoRooms = [
  { id: 1, hotelName: 'Sunrise Inn', roomType: 'Single', rating: 4.2, price: 1200, description: 'Cozy single room with a balcony.', imagePath: '/assets/images/rooms/single1.jpg' },
  { id: 2, hotelName: 'Blue Lagoon Hotel', roomType: 'Single', rating: 4.5, price: 1350, description: 'Comfortable single room with city view.', imagePath: '/assets/images/rooms/single2.jpg' },
  { id: 3, hotelName: 'Palm Breeze Resort', roomType: 'Single', rating: 4.0, price: 1100, description: 'Budget-friendly single room near the beach.', imagePath: '/assets/images/rooms/single3.jpg' },
  { id: 4, hotelName: 'Green Valley Stay', roomType: 'Single', rating: 4.3, price: 1400, description: 'Quiet single room surrounded by greenery.', imagePath: '/assets/images/rooms/single4.jpg' },
  { id: 5, hotelName: 'Starline Suites', roomType: 'Single', rating: 4.1, price: 1250, description: 'Single room with modern interiors.', imagePath: '/assets/images/rooms/single5.jpg' },

  { id: 6, hotelName: 'Royal Orchid', roomType: 'Double', rating: 4.6, price: 2200, description: 'Spacious double room with a king-size bed.', imagePath: '/assets/images/rooms/double1.jpg' },
  { id: 7, hotelName: 'Seaside Retreat', roomType: 'Double', rating: 4.4, price: 2100, description: 'Double room with a sea-facing balcony.', imagePath: '/assets/images/rooms/double2.jpg' },
  { id: 8, hotelName: 'Mountain View Inn', roomType: 'Double', rating: 4.3, price: 2000, description: 'Double room overlooking the mountains.', imagePath: '/assets/images/rooms/double3.jpg' },
  { id: 9, hotelName: 'Urban Bliss Hotel', roomType: 'Double', rating: 4.2, price: 2300, description: 'Modern double room in the city center.', imagePath: '/assets/images/rooms/double4.jpg' },
  { id: 10, hotelName: 'Golden Sands Resort', roomType: 'Double', rating: 4.5, price: 2250, description: 'Luxury double room near the beach.', imagePath: '/assets/images/rooms/double5.jpg' },

  { id: 11, hotelName: 'Ocean Pearl Suites', roomType: 'Suite', rating: 4.7, price: 3500, description: 'Luxury suite with private lounge.', imagePath: '/assets/images/rooms/suite1.jpg' },
  { id: 12, hotelName: 'Grand Heritage', roomType: 'Suite', rating: 4.8, price: 3700, description: 'Spacious suite with heritage-style decor.', imagePath: '/assets/images/rooms/suite2.jpg' },
  { id: 13, hotelName: 'Skyline Towers', roomType: 'Suite', rating: 4.6, price: 3600, description: 'Suite with panoramic city views.', imagePath: '/assets/images/rooms/suite3.jpg' },
  { id: 14, hotelName: 'Elite Palace', roomType: 'Suite', rating: 4.9, price: 4000, description: 'Presidential suite with premium services.', imagePath: '/assets/images/rooms/suite4.jpg' },
  { id: 15, hotelName: 'Riverside Resort', roomType: 'Suite', rating: 4.7, price: 3550, description: 'Suite with river-facing balcony.', imagePath: '/assets/images/rooms/suite5.jpg' },

  { id: 16, hotelName: 'Silver Crown', roomType: 'Deluxe', rating: 4.6, price: 2800, description: 'Deluxe room with elegant interiors.', imagePath: '/assets/images/rooms/deluxe1.jpg' },
  { id: 17, hotelName: 'Lakeside Inn', roomType: 'Deluxe', rating: 4.5, price: 2900, description: 'Deluxe room overlooking the lake.', imagePath: '/assets/images/rooms/deluxe2.jpg' },
  { id: 18, hotelName: 'The Grand Lotus', roomType: 'Deluxe', rating: 4.7, price: 3000, description: 'Deluxe room with spa access.', imagePath: '/assets/images/rooms/deluxe3.jpg' },
  { id: 19, hotelName: 'Royal Crown Stay', roomType: 'Deluxe', rating: 4.4, price: 2750, description: 'Deluxe room in a prime location.', imagePath: '/assets/images/rooms/deluxe4.jpg' },
  { id: 20, hotelName: 'Sunshine Residency', roomType: 'Deluxe', rating: 4.3, price: 2850, description: 'Deluxe room with modern amenities.', imagePath: '/assets/images/rooms/deluxe5.jpg' },
];


  useEffect(() => {
    const fetchRooms = async () => {
      try {
        const response = await ApiService.getAllRooms();
        const allRooms = response?.roomList && response.roomList.length > 0 ? response.roomList : demoRooms;
        setRooms(allRooms);
        setFilteredRooms(allRooms);
        // Set unique locations
        const uniqueLocations = Array.from(new Set(allRooms.map(room => room.hotelName)));
        setLocations(uniqueLocations);
      } catch (error) {
        console.error('Error fetching rooms:', error.message);
        setRooms(demoRooms);
        setFilteredRooms(demoRooms);
        const uniqueLocations = Array.from(new Set(demoRooms.map(room => room.hotelName)));
        setLocations(uniqueLocations);
      }
    };

    const fetchRoomTypes = async () => {
      try {
        const types = await ApiService.getRoomTypes();
        setRoomTypes(types.length > 0 ? types : ['Single', 'Double', 'Suite', 'Deluxe']);
      } catch (error) {
        console.error('Error fetching room types:', error.message);
        setRoomTypes(['Single', 'Double', 'Suite', 'Deluxe']);
      }
    };

    fetchRooms();
    fetchRoomTypes();
  }, []);

  const handleLocationChange = (e) => {
    const location = e.target.value;
    setSelectedLocation(location);
    filterRooms(location, selectedRoomType);
    setShowRooms(location !== '');
  };

  const handleRoomTypeChange = (e) => {
    const type = e.target.value;
    setSelectedRoomType(type);
    filterRooms(selectedLocation, type);
  };

  const filterRooms = (location, type) => {
    let filtered = rooms;
    if (location) {
      filtered = filtered.filter((room) => room.hotelName === location);
    }
    if (type) {
      filtered = filtered.filter((room) => room.roomType === type);
    }
    setFilteredRooms(filtered);
  };

  return (
    <div className='all-rooms'>
      <h2>All Rooms</h2>
      <div className='filters'>
        <div className='filter-group'>
          <label>Select Location:</label>
          <select value={selectedLocation} onChange={handleLocationChange}>
            <option value="">Select Location</option>
            {locations.map((location) => (
              <option key={location} value={location}>
                {location}
              </option>
            ))}
          </select>
        </div>
        <div className='filter-group'>
          <label>Filter by Room Type:</label>
          <select value={selectedRoomType} onChange={handleRoomTypeChange}>
            <option value="">All</option>
            {roomTypes.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </div>
      </div>

      {showRooms ? (
        filteredRooms.length > 0 ? (
          <RoomResult roomSearchResults={filteredRooms} />
        ) : (
          <p>No rooms found for this selection.</p>
        )
      ) : (
        <p>Please select a location to view rooms.</p>
      )}
    </div>
  );
};

export default AllRoomsPage;
