import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import ApiService from '../../service/ApiService';

const AdminPage = () => {
  const [adminName, setAdminName] = useState('');
  const [adminEmail, setAdminEmail] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAdmin = async () => {
      try {
        const response = await ApiService.getUserProfile();
        setAdminName(response.user.name);
        setAdminEmail(response.user.email);

        // Check if the user is the admin
        if (
          response.user.name !== "2300033208" ||
          response.user.email !== "2300033208@kluniversity.in"
        ) {
          navigate("/"); // redirect non-admins to home
        }
      } catch (error) {
        console.error("Error fetching admin details:", error.message);
        navigate("/"); // fallback if error
      }
    };
    fetchAdmin();
  }, [navigate]);

  return (
    <div className="admin-page">
      <h1 className="welcome-message">Welcome, {adminName}</h1>
      <div className="admin-actions">
        <button className="admin-button" onClick={() => navigate('/admin/manage-rooms')}>
          Manage Rooms
        </button>
        <button className="admin-button" onClick={() => navigate('/admin/manage-bookings')}>
          Manage Bookings
        </button>
      </div>
    </div>
  );
};

export default AdminPage;
