import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import './HomePage.css';




const HomePage = () => {
    const navigate = useNavigate();
    const [state, setState] = useState('');
    const [city, setCity] = useState('');
    const [cities, setCities] = useState([]);

    const statesAndCities = {

        'Maharashtra': ['Mumbai', 'Pune', 'Nagpur'],
        'Karnataka': ['Bangalore', 'Mysore', 'Mangalore'],
        'Tamil Nadu': ['Chennai', 'Coimbatore', 'Madurai'],
        'Delhi': ['Delhi'],
        'Uttar Pradesh': ['Lucknow', 'Kanpur', 'Agra'],
        'West Bengal': ['Kolkata', 'Howrah'],
        'Gujarat': ['Ahmedabad', 'Surat', 'Vadodara'],
        'Rajasthan': ['Jaipur', 'Udaipur', 'Jodhpur'],
        'Punjab': ['Chandigarh', 'Amritsar', 'Ludhiana'],
        'Haryana': ['Gurgaon', 'Faridabad', 'Panipat'],
        'Andhra Pradesh': ['Vijayawada', 'Visakhapatnam', 'Guntur', 'Tirupati'],
        'Telangana': ['Hyderabad', 'Warangal', 'Nizamabad', 'Karimnagar']
    };

    const handleStateChange = (e) => {
        const selectedState = e.target.value;
        setState(selectedState);
        setCity('');
        setCities(statesAndCities[selectedState] || []);
    };

    const handleSearch = () => {
        if (state && city) {
            navigate('/rooms', { state: { state, city } });
        }
    };

    return (
        <div className="home">
            {/* HEADER / BANNER ROOM SECTION */}
            <section>
                <header className="header-banner">

                    <div className="overlay"></div>
                    <div className="animated-texts overlay-content">
                        <h1>
                            Welcome to <span className="phegon-color"> Hotel Selector</span>
                        </h1><br />
                        <h3>Step into a haven of comfort and care</h3>
                    </div>
                </header>
            </section>

            {/* SEARCH/FIND AVAILABLE ROOM SECTION */}
            <section>
                <div className="search-container">
                    <div className="search-field">
                        <label>State</label>
                        <select value={state} onChange={handleStateChange}>
                            <option value="">
                                Select State
                            </option>
                            {Object.keys(statesAndCities).map((st) => (
                                <option key={st} value={st}>
                                    {st}
                                </option>
                            ))}
                        </select>
                    </div>
                    <div className="search-field">
                        <label>City</label>
                        <select value={city} onChange={(e) => setCity(e.target.value)} disabled={!state}>
                            <option value="">
                                Select City
                            </option>
                            {cities.map((ct) => (
                                <option key={ct} value={ct}>
                                    {ct}
                                </option>
                            ))}
                        </select>
                    </div>
                    <button className="home-search-button" onClick={handleSearch}>
                        Search Rooms
                    </button>
                </div>
            </section>

            <h4><a className="view-rooms-home" href="/rooms">All Rooms</a></h4>

            <h2 className="home-services">Services at <span className="hotel selector"> Hotel Selector</span></h2>

            {/* SERVICES SECTION */}
            <section className="service-section"><div className="service-card">

                <div className="service-details">
                    <h3 className="service-title">Air Conditioning</h3>
                    <p className="service-description">Stay cool and comfortable throughout your stay with our individually controlled in-room air conditioning.</p>
                </div>
            </div>
                <div className="service-card">

                    <div className="service-details">
                        <h3 className="service-title"> Bar and restaurant</h3>
                        <p className="service-description">Enjoy a convenient selection of beverages and snacks stocked in your room's mini bar with no additional cost.</p>
                    </div>
                </div>
                <div className="service-card">

                    <div className="service-details">
                        <h3 className="service-title">Parking</h3>
                        <p className="service-description">We offer on-site parking for your convenience . Please inquire about valet parking options if available.</p>
                    </div>
                </div>
                <div className="service-card">

                    <div className="service-details">
                        <h3 className="service-title">WiFi</h3>
                        <p className="service-description">Stay connected throughout your stay with complimentary high-speed Wi-Fi access available in all guest rooms and public areas.</p>
                    </div>
                </div>

            </section>
            {/* FEATURED ROOMS SECTION */}
            <section>
                <h2 style={{ textAlign: 'center', margin: '40px 0 20px 0' }}>Featured Rooms</h2>
                <div style={{ display: 'flex', flexDirection: 'column', gap: '20px', maxWidth: '1200px', margin: '0 auto', padding: '0 20px' }}>
                    {demoRooms.slice(0, 3).map(room => (
                        <div key={room.id} style={{
                            display: 'flex',
                            border: '1px solid #e0e0e0',
                            borderRadius: '12px',
                            overflow: 'hidden',
                            backgroundColor: '#fff',
                            boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
                            transition: 'box-shadow 0.3s'
                        }}>
                            <img
                                src={room.imagePath}
                                alt={`${room.hotelName} - ${room.roomType}`}
                                style={{ width: '250px', height: '180px', objectFit: 'cover' }}
                            />
                            <div style={{ flex: 1, padding: '20px', display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
                                <h3>{room.hotelName}</h3>
                                <p>Type: {room.roomType}</p>
                                <p>Price: â‚¹{room.price} / night</p>
                                <p>Availability: Available</p>
                            </div>
                            <div style={{ display: 'flex', alignItems: 'center', padding: '20px', justifyContent: 'flex-end' }}>
                                <button style={{
                                    padding: '12px 20px',
                                    backgroundColor: '#007bff',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '8px',
                                    cursor: 'pointer',
                                    fontWeight: '500'
                                }}>Book Now</button>
                            </div>
                        </div>
                    ))}
                </div>
            </section>
        </div>
    );
}

export default HomePage;
